import React from 'react';
import axios from 'axios';
import Spinner from './components/Spinner';
import SearchBar from './components/SearchBar';
import PersonList from './components/PersonList';
import './App.css';

class App extends React.Component {
  state = {
    persons:[],
    tempPersons: [],
    isSearchResultEmpty: false
  }

  componentDidMount(){
    var that = this;

    //purposely delaying the call
    setTimeout(function(){
      that.fetchUserData();
    }, 2000);
  }

  fetchUserData(){
    axios.get('https://jsonplaceholder.typicode.com/users ')
    .then(res => {
      this.setState(
        {
          persons: res.data,
          tempPersons: res.data
        }
        );
    });
  }

  showSearchBar = (persons) => {

      return  <SearchBar onSearchChange={this.onHandleChange}/>;

  }

  showMainLayout = (persons) => {
    if(persons.length === 0){
      return <Spinner description = "Loading" />;
    }else{
      return <PersonList persons = {this.state.persons} />;
    }
  }

  onHandleChange = (event) => {
    var that  = this;
    let tmpList = [];
    if(event.target.value !== ""){
      tmpList = this.state.persons.filter(person => {
        return person.name.includes(event.target.value) 
            || person.username.includes(event.target.value)
            || person.email.includes(event.target.value);
      })

    }else{
      tmpList = this.state.tempPersons
    }

    this.setState({
      isSearchResultEmpty: tmpList.length === 0 ? true : false,
      persons: tmpList
    });
  }

  render(){
    const persons = this.state.persons;
    const isSearchResultEmpty = this.state.isSearchResultEmpty;
    return(

          <div className = "container">
            {this.showSearchBar(persons)}


        {
          isSearchResultEmpty
          ? <div><h5>No results...</h5></div>
          : this.showMainLayout(persons)
        }

      </div>


    );
  }
}

export default App;
