import React from 'react';
import axios from 'axios';
import Spinner from './components/Spinner';
import Error from './components/Error';
import SearchBar from './components/SearchBar';
import PersonList from './components/PersonList';
import Dialog from './components/Dialog';
import './css/style-main.css';

class App extends React.Component {
  state = {
    persons:[],
    tempPersons: [],
    isSearchResultEmpty: false,
    dialogComponent: {}
  }

  componentDidMount(){
    var that = this;

    //purposely delaying the call
    setTimeout(function(){
      that.fetchUserData();
    }, 2000);
  }

  fetchUserData(){
    axios.get('https://jsonplaceholder.typicode.com/users ')
    .then(res => {
      this.setState(
        {
          persons: res.data,
          tempPersons: res.data
        }
        );
    });
  }

  showMainLayout = (persons, isSearchResultEmpty) => {
    if(!isSearchResultEmpty && persons.length === 0){
      return <Spinner description = "Fetching users data..." />;
    }else{
      return(
      <div className = "mainLayout">
        <SearchBar onSearchChange={this.onHandleChange}/>
        {
        isSearchResultEmpty 
        ?  <Error description="No results" />
        :  <PersonList persons = {this.state.persons} onCallButtonClick={this.onCallButtonClick}/>
        }
      </div>
      );

    }
  }

  onCallButtonClick = (event) => {
    let dialogBox;
    // if(event.currentTarget.value.length > 0){
    //   dialogBox = <Dialog person={event.currentTarget.value} onCallButtonClick = {this.onCallButtonClick}/>;
    // }
    this.setState({
      dialogComponent : dialogBox
    });
  }

  onHandleChange = (event) => {
    var that  = this;
    let tmpList = [];
    if(event.target.value !== ""){
      tmpList = this.state.persons.filter(person => {
        return person.name.includes(event.target.value) 
            || person.username.includes(event.target.value)
            || person.email.includes(event.target.value);
      })

    }else{
      tmpList = this.state.tempPersons
    }

    this.setState({
      isSearchResultEmpty: tmpList.length === 0 ? true : false,
      persons: tmpList
    });
  }

  render(){
    const persons = this.state.persons;
    const isSearchResultEmpty = this.state.isSearchResultEmpty;
    let dialogBox = this.state.dialogComponent;
    // if(Object.entries(this.state.dialogObject).length !== 0){
    //   dialogBox = <Dialog person={this.state.dialogObject} onCallButtonClick = {this.onCallButtonClick}/>;
    // }
    return(
      <div className = "layout-container">
        {dialogBox}
        {this.showMainLayout(persons, isSearchResultEmpty)}
      </div>


    );
  }
}

export default App;
