import React from 'react';

const Error = (props) => {
    return(
        <div style = {styles.errorLayout}>
            <i className = "fa fa-window-close"/>
            <span>{props.description}</span>
        </div>
    );

}

const styles = {
    errorLayout: {
        width: "90%",
        height: "100%",
        background: "green",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems:"center"

    }
}

export default Error;
