import '../css/style-persons.css';
import React from 'react';
import Button from './Button';

class PersonCard extends React.Component {
    createPersonCallObject(props){
        return {
            name: props.person.name,
            phone: props.person.phone
        };
    }

    render (){
        return (
        <div className = "cardLayout">
            <div className = "row">
                <div className = "leftLayout column">
                    <i className = "fa fa-user card-icon-layout"/>
                </div>
                <div className = "rightLayout column">
                    <h4>{this.props.person.name}</h4>
                    <span><b>Username:</b> {this.props.person.username}</span>
                    <br></br>
                    <span><b>Email: </b> {this.props.person.email}</span>
                </div>
            </div>
            <div className = "locationInfoSection">
            <div className = "row">
                <div className = "leftLayout column">
                <i className = "fa fa-map-marker card-icon-layout"/>
                </div>
                <div className = "rightLayout column">
                <h5>{this.props.person.address.street}</h5>
                    <h5>{this.props.person.address.suite}</h5>
                    <h5>{this.props.person.address.city}</h5>
                    <h5>{this.props.person.address.zipcode}</h5>
                </div>
            </div>
            </div>
            <div className = "callSection">
                <Button 
                iconName = "fa-phone" 
                onButtonClick={this.props.onCallButtonClick}
                primaryColor = "red"
                hoverColor = "green">
                    Call {this.props.person.name}
                </Button>
                {/* <button className = "callButton" value={this.props.person.phone} onClick = {this.props.onCallButtonClick}>
                  <i className = "fa fa-phone card-icon-layout"/>
                  <span style ={{marginLeft: "20px"}}>Call {this.props.person.name}</span>
                </button> */}
            </div>
        </div>
        );
    }
}

export default PersonCard;
