import '../css/style-persons.css';
import React from 'react';
import Button from './Button';

class PersonCard extends React.Component {
    createPersonCallObject(props){
        return {
            name: props.person.name,
            phone: props.person.phone
        };
    }

    render (){
        return (
        <div className = "card-layout">
            <div className = "row">
                <div className = "left-layout column">
                    <i className = "fa fa-user card-icon-layout"/>
                </div>
                <div className = "right-layout column">
                    <h4>{this.props.person.name}</h4>
                    <span><b>Username:</b></span>
                    <div>{this.props.person.username}</div>
                    <br></br>
                    <span><b>Email: </b></span>
                    <div>{this.props.person.email}</div>
                </div>
            </div>
            <div className = "location-section">
            <div className = "row">
                <div className = "left-layout column">
                <i className = "fa fa-map-marker card-icon-layout"/>
                </div>
                <div className = "right-layout column">
                <h5>{this.props.person.address.street}</h5>
                    <h5>{this.props.person.address.suite}</h5>
                    <h5>{this.props.person.address.city}</h5>
                    <h5>{this.props.person.address.zipcode}</h5>
                </div>
            </div>
            </div>
            <div className = "call-section">
                <Button
                value = {this.props.person.phone}
                iconName = "fa-phone" 
                onButtonClick={this.props.onCallButtonClick}
                primaryColor = "#f1e8c3"
                hoverColor = "green">
                    Call {this.props.person.name}
                </Button>
            </div>
        </div>
        );
    }
}

export default PersonCard;
