import '../css/style-button.css'
import React from 'react';

class Button extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            primaryColor: this.props.primaryColor,
            textColor: 'black'
        }
    }

    onHover = () => {
        this.setState(
            {
                primaryColor: this.props.hoverColor,
                textColor: "white"
            }
        );
    }

    onHoverOff = () => {
        this.setState(
            {
                primaryColor: this.props.primaryColor,
                textColor: "black"
            }
        );
    }


    render(){
        const style = {
            background: `${this.state.primaryColor}`,
            color: `${this.state.textColor}`,
            borderRadius: '10px',
            transition: 'all .3s',
            paddingLeft: '15px',
            paddingRight: '15px',
            paddingTop: '10px',
            paddingBottom: '10px'
        }
        return(
            <button className = "button-layout" 
            onClick = {this.props.onButtonClick} 
            onMouseOver = {this.onHover}
            onMouseLeave = {this.onHoverOff}
            style={style}>
                <i className = {`fa ${this.props.iconName}`}/>
                <span style ={{marginLeft: "20px"}}>{this.props.children}</span>
            </button>
        );
    }

}



export default Button;
