import React from 'react';

const Error = (props) => {
    return(
        <div style = {styles.errorLayout}>
            <i className = "fa fa-window-close" style = {styles.errorIconLayout}/>
            <span>{props.description}</span>
        </div>
    );

}

const styles = {
    errorLayout: {
        width: "87%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems:"center",
        marginTop:"15%"
    },
    errorIconLayout: {
        fontSize: "120px"
    }
}

export default Error;
