import React from 'react';
import Spinner from 'react-bootstrap/Spinner';
import '../App.css';

const SpinnerComponent = (props) => {
    return (
        <div className = "spinnerContainer">
            <div className ="spinnerItem">
            <Spinner 
                animation="border" 
                role="status"
                style={style.spinnerSize}
             />
            <span style = {styles.descriptionLayout}>{props.description}</span>
            </div>
        </div>

    );
}

let styles = {
    descriptionLayout : {
        display: "block"
    },
    spinnerSize: {
        width: "3erm",
        height: "3erm"
    }
}

export default SpinnerComponent;
