import React from 'react';
import axios from 'axios';
import Spinner from './components/Spinner';
import SearchBar from './components/SearchBar';
import PersonList from './components/PersonList';
import './App.css';

class App extends React.Component {
  state = {
    persons:[],
    tempPersons: []
  }

  componentDidMount(){
    var that = this;

    //purposely delaying the call
    setTimeout(function(){
      that.fetchUserData();
    }, 0);
  }

  fetchUserData(){
    axios.get('https://jsonplaceholder.typicode.com/users ')
    .then(res => {
      this.setState(
        {
          persons: res.data,
          tempPersons: res.data
        }
        );
    });
  }

  onSearchInput(event){
    var that  = this;
    if(event.target.value.length === 0){
      this.setState(
        {
          persons: this.state.tempPersons
        }
      );
    }else{
      let tempArray = that.state.persons;
      tempArray.filter(person => {
        return person.person.name === 'event.target.value';
      });
    }
    console.log(event.target.value);

  }

  render(){
    const isPersonEmpty = this.state.persons;
    return(
      <div>

          <div className = "container">
          <SearchBar onSearchChange={this.onSearchInput}/>

        {
          isPersonEmpty.length === 0
          ? <Spinner description = "Loading" /> 
          : <PersonList persons = {this.state.persons} />
        }

      </div>
      </div>

    );
  }
}

export default App;
